    def search_files(self):
        # Clear previous results
        for i in reversed(range(self.scrollAreaLayout.count())):
            self.scrollAreaLayout.itemAt(i).widget().setParent(None)

        search_term = self.lineEdit.text()
        text_lines = file_loader()
        results = file_finder(search_term, text_lines)

        # Display new results
        for result in results:
            label = QtWidgets.QLabel(result.strip())
            self.scrollAreaLayout.addWidget(label)

    def update_bibliography(self):
        bib_parser(path)

    def alles_state_changed(self, state):
        is_checked = self.checkBox.isChecked()
        for cb in self.other_checkboxes:
            cb.blockSignals(True)
            cb.setChecked(is_checked)
            cb.blockSignals(False)

    def other_state_changed(self, state):
        self.checkBox.blockSignals(True)
        if all(cb.isChecked() for cb in self.other_checkboxes):
            self.checkBox.setChecked(True)
        else:
            self.checkBox.setChecked(False)
        self.checkBox.blockSignals(False)

